    private List<Transaction> createDummyTransactions() {
        List<Transaction> list = new ArrayList<>();
        // Add some transactions from different dates
        list.add(new Transaction("1000", "25.50", "2025-08-03 09:00"));
        list.add(new Transaction("1001", "25.50", "2025-08-03 09:30"));
        list.add(new Transaction("1002", "42.99", "2025-08-03 10:15"));
        list.add(new Transaction("1003", "15.75", "2025-08-03 11:20"));
        list.add(new Transaction("1004", "89.99", "2025-08-03 12:45"));
        list.add(new Transaction("1005", "33.25", "2025-08-03 13:30"));

        // Filter to keep only transactions before today
        List<Transaction> filteredTransactions = new ArrayList<>();
        LocalDateTime today = LocalDateTime.now().withHour(0).withMinute(0).withSecond(0).withNano(0);

        for (Transaction t : list) {
            LocalDateTime transactionDate = LocalDateTime.parse(t.getDateTime(), formatter);
            if (transactionDate.isBefore(today)) {
                filteredTransactions.add(t);
            }
        }

        return filteredTransactions;
    }

