package gr.android.softposecr.ui.checkoutScreen;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import gr.android.softposecr.R;
import gr.android.softposecr.databinding.FragmentEreceiptBinding;

public class EReceiptFragment extends Fragment {
    private FragmentEreceiptBinding binding;

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container,
                            Bundle savedInstanceState) {
        binding = FragmentEreceiptBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        setupViews();
        setupClickListeners();
    }

    private void setupViews() {
        // Set hints color for EditTexts
        binding.emailEditText.setHintTextColor(requireContext().getColor(R.color.icon_text));
        binding.phoneEditText.setHintTextColor(requireContext().getColor(R.color.icon_text));
    }

    private void setupClickListeners() {
        binding.continueButton.setOnClickListener(v -> {
            String email = binding.emailEditText.getText().toString().trim();
            String phone = binding.phoneEditText.getText().toString().trim();

            if (validateInputs(email, phone)) {
                // TODO: Handle the continue action, for example navigate to next screen
                // For now, just show a toast
                Toast.makeText(requireContext(), "Continuing with email: " + email, Toast.LENGTH_SHORT).show();
            }
        });
    }

    private boolean validateInputs(String email, String phone) {
        if (email.isEmpty()) {
            binding.emailEditText.setError("Email is required");
            return false;
        }
        if (!android.util.Patterns.EMAIL_ADDRESS.matcher(email).matches()) {
            binding.emailEditText.setError("Please enter a valid email");
            return false;
        }
        if (phone.isEmpty()) {
            binding.phoneEditText.setError("Phone number is required");
            return false;
        }
        return true;
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }
}

package gr.android.softposecr.ui.checkoutScreen;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;

import gr.android.softposecr.R;
import gr.android.softposecr.databinding.FragmentEreceiptBinding;

public class EReceiptFragment extends Fragment {
    private FragmentEreceiptBinding binding;

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container,
                            Bundle savedInstanceState) {
        binding = FragmentEreceiptBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        setupViews();
        setupClickListeners();
    }

    private void setupViews() {
        // Set hints color for EditTexts
        binding.emailEditText.setHintTextColor(requireContext().getColor(R.color.icon_text));
        binding.phoneEditText.setHintTextColor(requireContext().getColor(R.color.icon_text));

        // Set input types
        binding.emailEditText.setInputType(android.text.InputType.TYPE_TEXT_VARIATION_EMAIL_ADDRESS);
        binding.phoneEditText.setInputType(android.text.InputType.TYPE_CLASS_PHONE);
    }

    private void setupClickListeners() {
        binding.backArrowButton.setOnClickListener(v -> {
            // Navigate back
            Navigation.findNavController(v).navigateUp();
        });

        binding.continueButton.setOnClickListener(v -> {
            String email = binding.emailEditText.getText().toString().trim();
            String phone = binding.phoneEditText.getText().toString().trim();

            if (validateInputs(email, phone)) {
                // TODO: Save the contact information and navigate to next screen
                Toast.makeText(requireContext(), "Processing contact information...", Toast.LENGTH_SHORT).show();
                // Navigation.findNavController(v).navigate(R.id.action_eReceiptFragment_to_nextFragment);
            }
        });
    }

    private boolean validateInputs(String email, String phone) {
        boolean isValid = true;

        if (email.isEmpty()) {
            binding.emailEditText.setError("Email is required");
            isValid = false;
        } else if (!android.util.Patterns.EMAIL_ADDRESS.matcher(email).matches()) {
            binding.emailEditText.setError("Please enter a valid email address");
            isValid = false;
        } else {
            binding.emailEditText.setError(null);
        }

        if (phone.isEmpty()) {
            binding.phoneEditText.setError("Phone number is required");
            isValid = false;
        } else if (!android.util.Patterns.PHONE.matcher(phone).matches()) {
            binding.phoneEditText.setError("Please enter a valid phone number");
            isValid = false;
        } else {
            binding.phoneEditText.setError(null);
        }

        return isValid;
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }
}
