package gr.android.softposecr.ui.detailsScreen;

import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;

import java.util.Locale;

import gr.android.softposecr.databinding.FragmentItemDetailsBinding;
import gr.android.softposecr.ui.homeScreen.ItemViewModel;

public class ItemDetailsFragment extends Fragment {
    private ItemViewModel viewModel;
    private FragmentItemDetailsBinding binding;
    private String currentNotes = "";

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        binding = FragmentItemDetailsBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        setupViews();
        updateUI();
    }

    private void setupViews() {
        binding.backArrowButton.setOnClickListener(v ->
                Navigation.findNavController(binding.getRoot()).navigateUp());

        // Setup submit button
        binding.submitNotes.setOnClickListener(v -> {
            String notes = binding.itemNotes.getText().toString();
            if (!notes.equals("Notes..") && !notes.isEmpty()) {
                // Here you can handle the notes submission
                // For example, save to ViewModel or database
                binding.itemNotes.clearFocus();
            }
        });

        // Setup notes EditText
        binding.itemNotes.setOnFocusChangeListener((v, hasFocus) -> {
            if (hasFocus && binding.itemNotes.getText().toString().equals("Notes..")) {
                binding.itemNotes.setText("");
            } else if (!hasFocus && binding.itemNotes.getText().toString().isEmpty()) {
                binding.itemNotes.setHint("Notes..");
            }
        });

        binding.itemNotes.addTextChangedListener(new TextWatcher() {
            @Override
            public void beforeTextChanged(CharSequence s, int start, int count, int after) {}

            @Override
            public void onTextChanged(CharSequence s, int start, int before, int count) {}

            @Override
            public void afterTextChanged(Editable s) {
                if (!s.toString().equals("Notes..")) {
                    currentNotes = s.toString();
                }
            }
        });
    }

    private void updateUI() {
        if (getArguments() != null) {
            String itemTitle = getArguments().getString("ITEM_TITLE", "");
            String itemDescription = getArguments().getString("ITEM_DESCRIPTION", "");
            int itemPoster = getArguments().getInt("ITEM_POSTER", 0);
            double itemPrice = getArguments().getDouble("ITEM_PRICE", 0.0);

            binding.itemTitle.setText(itemTitle);
            binding.itemDescription.setText(itemDescription);
            binding.itemPoster.setImageResource(itemPoster);
            binding.itemPrice.setText(String.format(Locale.getDefault(), "%.2fâ‚¬", itemPrice));
        }
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }
}

package gr.android.softposecr.ui.detailsScreen;

import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.lifecycle.ViewModelProvider;
import androidx.navigation.Navigation;

import java.util.Locale;

import gr.android.softposecr.databinding.FragmentItemDetailsBinding;
import gr.android.softposecr.ui.homeScreen.ItemViewModel;

public class ItemDetailsFragment extends Fragment {
    private ItemViewModel viewModel;
    private FragmentItemDetailsBinding binding;
    private String currentNotes = "";
    private String currentItemTitle = "";

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        binding = FragmentItemDetailsBinding.inflate(inflater, container, false);
        viewModel = new ViewModelProvider(requireActivity()).get(ItemViewModel.class);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        setupViews();
        setupObservers();
        updateUI();
    }

    private void setupObservers() {
        // Observe cart changes to keep counter updated
        viewModel.getCartItems().observe(getViewLifecycleOwner(), items -> {
            updateCounter();
        });
    }

    private void setupViews() {
        binding.backArrowButton.setOnClickListener(v ->
                Navigation.findNavController(binding.getRoot()).navigateUp());

        // Setup counter buttons
        binding.plusButton.setOnClickListener(v -> {
            if (!currentItemTitle.isEmpty()) {
                viewModel.incrementItemQuantity(currentItemTitle);
                updateCounter();
            }
        });

        binding.minusButton.setOnClickListener(v -> {
            if (!currentItemTitle.isEmpty()) {
                viewModel.decrementItemQuantity(currentItemTitle);
                updateCounter();
            }
        });

        // Setup submit button
        binding.submitNotes.setOnClickListener(v -> {
            String notes = binding.itemNotes.getText().toString();
            if (!notes.equals("Notes..") && !notes.isEmpty()) {
                // Here you can handle the notes submission
                // For example, save to ViewModel or database
                binding.itemNotes.clearFocus();
            }
        });

        // Setup notes EditText
        binding.itemNotes.setOnFocusChangeListener((v, hasFocus) -> {
            if (hasFocus && binding.itemNotes.getText().toString().equals("Notes..")) {
                binding.itemNotes.setText("");
            } else if (!hasFocus && binding.itemNotes.getText().toString().isEmpty()) {
                binding.itemNotes.setHint("Notes..");
            }
        });

        binding.itemNotes.addTextChangedListener(new TextWatcher() {
            @Override
            public void beforeTextChanged(CharSequence s, int start, int count, int after) {}

            @Override
            public void onTextChanged(CharSequence s, int start, int before, int count) {}

            @Override
            public void afterTextChanged(Editable s) {
                if (!s.toString().equals("Notes..")) {
                    currentNotes = s.toString();
                }
            }
        });
    }

    private void updateCounter() {
        if (!currentItemTitle.isEmpty()) {
            int quantity = viewModel.getItemQuantityByTitle(currentItemTitle);
            binding.itemCounter.setText(String.valueOf(quantity));
        }
    }

    private void updateUI() {
        if (getArguments() != null) {
            String itemTitle = getArguments().getString("ITEM_TITLE", "");
            String itemDescription = getArguments().getString("ITEM_DESCRIPTION", "");
            int itemPoster = getArguments().getInt("ITEM_POSTER", 0);
            double itemPrice = getArguments().getDouble("ITEM_PRICE", 0.0);

            currentItemTitle = itemTitle;
            binding.itemTitle.setText(itemTitle);
            binding.itemDescription.setText(itemDescription);
            binding.itemPoster.setImageResource(itemPoster);
            binding.itemPrice.setText(String.format(Locale.getDefault(), "%.2fâ‚¬", itemPrice));

            // Initialize counter with current quantity
            updateCounter();
        }
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }
}

package gr.android.softposecr.ui.homeScreen;

import android.view.LayoutInflater;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.DiffUtil;
import androidx.recyclerview.widget.ListAdapter;
import androidx.recyclerview.widget.RecyclerView;
import java.util.Locale;
import gr.android.softposecr.databinding.ItemBinding;
import gr.android.softposecr.domain.models.Item;

public class ItemListAdapter extends ListAdapter<Item, ItemListAdapter.ItemViewHolder> {

    private final OnItemActionListener listener;
    private final ItemViewModel viewModel;

    public ItemListAdapter(OnItemActionListener listener, ItemViewModel viewModel) {
        super(new DiffCallback());
        this.listener = listener;
        this.viewModel = viewModel;
    }

    @NonNull
    @Override
    public ItemViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        ItemBinding binding = ItemBinding.inflate(LayoutInflater.from(parent.getContext()), parent, false);
        return new ItemViewHolder(binding);
    }

    @Override
    public void onBindViewHolder(@NonNull ItemViewHolder holder, int position) {
        Item item = getItem(position);
        holder.bind(item);
        holder.itemView.setOnClickListener(v -> listener.onItemClick(item.getTitle()));

        // Initialize counter with current quantity
        int currentQuantity = viewModel.getItemQuantity(item);
        holder.binding.itemCounter.setText(String.valueOf(currentQuantity));

        holder.binding.plusButton.setOnClickListener(v -> {
            listener.onPlusClick(item.getTitle());
            int count = Integer.parseInt(holder.binding.itemCounter.getText().toString());
            holder.binding.itemCounter.setText(String.valueOf(count + 1));
        });
        holder.binding.minusButton.setOnClickListener(v -> {
            listener.onMinusClick(item.getTitle());
            int count = Integer.parseInt(holder.binding.itemCounter.getText().toString());
            if (count > 0) {
                holder.binding.itemCounter.setText(String.valueOf(count - 1));
            }
        });
    }

    static class ItemViewHolder extends RecyclerView.ViewHolder {
        private final ItemBinding binding;

        ItemViewHolder(ItemBinding binding) {
            super(binding.getRoot());
            this.binding = binding;
        }

        void bind(Item item) {
            binding.itemTitle.setText(item.getTitle());
            binding.itemPrice.setText(String.format(Locale.getDefault(), "%.2fâ‚¬", item.getPrice()));
            binding.itemDescription.setText(item.getOverview());
            binding.itemPoster.setImageResource(item.getPosterPath());
            binding.itemCounter.setText("0"); // Reset counter when binding
        }
    }

    static class DiffCallback extends DiffUtil.ItemCallback<Item> {
        @Override
        public boolean areItemsTheSame(@NonNull Item oldItem, @NonNull Item newItem) {
            return oldItem.getTitle().equals(newItem.getTitle());
        }

        @Override
        public boolean areContentsTheSame(@NonNull Item oldItem, @NonNull Item newItem) {
            return oldItem.getTitle().equals(newItem.getTitle()) &&
                   oldItem.getOverview().equals(newItem.getOverview()) &&
                   oldItem.getPosterPath() == newItem.getPosterPath() &&
                   oldItem.getPrice() == newItem.getPrice();
        }
    }


    public interface OnItemActionListener {
        void onItemClick(String itemTitle);
        void onPlusClick(String itemTitle);
        void onMinusClick(String itemTitle);
    }
}

package gr.android.softposecr.ui.homeScreen;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.DiffUtil;
import androidx.recyclerview.widget.ListAdapter;
import androidx.recyclerview.widget.RecyclerView;
import java.util.Locale;
import gr.android.softposecr.databinding.ItemBinding;
import gr.android.softposecr.domain.models.Item;

public class ItemListAdapter extends ListAdapter<Item, ItemListAdapter.ItemViewHolder> {

    private final OnItemActionListener listener;
    private final ItemViewModel viewModel;

    public ItemListAdapter(OnItemActionListener listener, ItemViewModel viewModel) {
        super(new DiffCallback());
        this.listener = listener;
        this.viewModel = viewModel;
    }

    @NonNull
    @Override
    public ItemViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        ItemBinding binding = ItemBinding.inflate(LayoutInflater.from(parent.getContext()), parent, false);
        return new ItemViewHolder(binding);
    }

    @Override
    public void onBindViewHolder(@NonNull ItemViewHolder holder, int position) {
        Item item = getItem(position);
        holder.bind(item);
        holder.itemView.setOnClickListener(v -> {
            Bundle args = new Bundle();
            args.putString("ITEM_TITLE", item.getTitle());
            args.putString("ITEM_DESCRIPTION", item.getOverview());
            args.putInt("ITEM_POSTER", item.getPosterPath());
            args.putFloat("ITEM_PRICE", (float) item.getPrice());
            Navigation.findNavController(v).navigate(R.id.action_itemListFragment_to_itemDetailsFragment, args);
        });

        // Initialize counter with current quantity
        int currentQuantity = viewModel.getItemQuantity(item);
        holder.binding.itemCounter.setText(String.valueOf(currentQuantity));

        holder.binding.plusButton.setOnClickListener(v -> {
            listener.onPlusClick(item.getTitle());
            int count = Integer.parseInt(holder.binding.itemCounter.getText().toString());
            holder.binding.itemCounter.setText(String.valueOf(count + 1));
        });
        holder.binding.minusButton.setOnClickListener(v -> {
            listener.onMinusClick(item.getTitle());
            int count = Integer.parseInt(holder.binding.itemCounter.getText().toString());
            if (count > 0) {
                holder.binding.itemCounter.setText(String.valueOf(count - 1));
            }
        });
    }

    static class ItemViewHolder extends RecyclerView.ViewHolder {
        private final ItemBinding binding;

        ItemViewHolder(ItemBinding binding) {
            super(binding.getRoot());
            this.binding = binding;
        }

        void bind(Item item) {
            binding.itemTitle.setText(item.getTitle());
            binding.itemPrice.setText(String.format(Locale.getDefault(), "%.2fâ‚¬", item.getPrice()));
            binding.itemDescription.setText(item.getOverview());
            binding.itemPoster.setImageResource(item.getPosterPath());
            binding.itemCounter.setText("0"); // Reset counter when binding
        }
    }

    static class DiffCallback extends DiffUtil.ItemCallback<Item> {
        @Override
        public boolean areItemsTheSame(@NonNull Item oldItem, @NonNull Item newItem) {
            return oldItem.getTitle().equals(newItem.getTitle());
        }

        @Override
        public boolean areContentsTheSame(@NonNull Item oldItem, @NonNull Item newItem) {
            return oldItem.getTitle().equals(newItem.getTitle()) &&
                   oldItem.getOverview().equals(newItem.getOverview()) &&
                   oldItem.getPosterPath() == newItem.getPosterPath() &&
                   oldItem.getPrice() == newItem.getPrice();
        }
    }


    public interface OnItemActionListener {
        void onItemClick(String itemTitle);
        void onPlusClick(String itemTitle);
        void onMinusClick(String itemTitle);
    }
}

package gr.android.softposecr.ui.homeScreen;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.lifecycle.ViewModelProvider;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;

import java.util.Locale;

import dagger.hilt.android.AndroidEntryPoint;
import gr.android.softposecr.R;
import gr.android.softposecr.databinding.FragmentItemListBinding;
import gr.android.softposecr.domain.models.Item;

// implements ItemListAdapter.OnItemClickListener {
@AndroidEntryPoint
public class ItemListFragment extends Fragment implements ItemListAdapter.OnItemActionListener {

    private FragmentItemListBinding binding;

    private ItemListAdapter adapter;

    private ItemViewModel viewModel;

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        binding = FragmentItemListBinding.inflate(inflater, container, false);
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        viewModel = new ViewModelProvider(requireActivity()).get(ItemViewModel.class);
        setupRecyclerView();
        updateUI();
    }

    private void setupRecyclerView() {
        binding.recyclerView.setLayoutManager(new LinearLayoutManager(requireContext()));
        adapter = new ItemListAdapter(this, viewModel);
        binding.recyclerView.setAdapter(adapter);
        binding.recyclerView.setItemAnimator(null);

        // Set up search functionality
        binding.search.setOnQueryTextListener(new androidx.appcompat.widget.SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String query) {
                return false;
            }

            @Override
            public boolean onQueryTextChange(String newText) {
                viewModel.filterItems(newText);
                return true;
            }
        });

        viewModel.getFilteredItems().observe(getViewLifecycleOwner(), items -> {
            adapter.submitList(items);
        });
    }

    private void updateUI() {
        // Update cart total when quantities change
        viewModel.getCartTotal().observe(getViewLifecycleOwner(), total -> {
            binding.cartTotalText.setText(String.format(Locale.getDefault(), "\uD83D\uDED2 Cart: %.2fâ‚¬", total));
            if( total == 0.0f) {
                binding.cartTotalText.setVisibility(View.GONE);
            } else {
                binding.cartTotalText.setVisibility(View.VISIBLE);
            }
        });
        binding.cartTotalText.setOnClickListener(view -> {
            // Navigate to CartFragment when cart total is clicked
            Navigation.findNavController(binding.getRoot()).navigate(R.id.action_itemListFragment_to_cartFragment);
        });
        binding.backArrowButton.setOnClickListener(v -> {
            // Navigate back to the previous screen
            Navigation.findNavController(binding.getRoot()).navigateUp();
        });
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        binding = null;
    }

    @Override
    public void onItemClick(String itemTitle) {
        // Handle item click
        Bundle bundle = new Bundle();
        bundle.putString("ITEM_TITLE", itemTitle);
        bundle.putString("ITEM_DESCRIPTION", "Description of " + itemTitle);
        bundle.putInt("ITEM_POSTER", R.drawable.loading);
        bundle.putFloat("ITEM_PRICE", 0.00f);
        Navigation.findNavController(binding.getRoot()).navigate(R.id.action_itemListFragment_to_itemDetailsFragment, bundle);
    }

    @Override
    public void onPlusClick(String itemTitle) {
        // Handle plus button click
        viewModel.incrementItemQuantity(itemTitle);
    }

    @Override
    public void onMinusClick(String itemTitle) {
        // Handle minus button click
        viewModel.decrementItemQuantity(itemTitle);
    }
}

